  2→import * as fs from 'fs';
  3→import { ExtensionContext, workspace, window, commands, TextEdit as VSCodeTextEdit, FormattingOptions as VSCodeFormattingOptions } from 'vscode';
  4→import {
  5→  LanguageClient,
