  7→  ServerOptions,
  8→  TransportKind,
  9→} from 'vscode-languageclient/node';
 10→import {
 11→  CustomCodeTagConfig,
 12→  parseCustomCodeTags,
 13→  generateInjectionGrammar,
 14→  computeHash,
 15→} from './grammarGenerator';
 16→
 17→let client: LanguageClient;
 18→const outputChannel = window.createOutputChannel('HTML Mustache');
 19→
 20→function log(message: string) {
 21→  const timestamp = new Date().toISOString();
 22→  outputChannel.appendLine(`[${timestamp}] ${message}`);
 23→}
 24→
 25→function updateInjectionGrammar(
 26→  syntaxTags: CustomCodeTagConfig[],
 27→  grammarPath: string,
 28→): boolean {
 29→  const newContent = generateInjectionGrammar(syntaxTags);
 30→  const newHash = computeHash(newContent);
 31→
 32→  let existingHash = '';
 33→  try {
 34→    const existing = fs.readFileSync(grammarPath, 'utf-8');
 35→    existingHash = computeHash(existing);
 36→  } catch {
 37→    // File doesn't exist yet
 38→  }
 39→
 40→  if (newHash === existingHash) {
 41→    return false;
 42→  }
 43→
 44→  fs.mkdirSync(path.dirname(grammarPath), { recursive: true });
 45→  fs.writeFileSync(grammarPath, newContent, 'utf-8');
 46→  log(`Updated injection grammar at ${grammarPath}`);
 47→  return true;
 48→}
 49→
 50→function promptReload() {
 51→  window
 52→    .showInformationMessage(
 53→      'Embedded language grammar updated. Reload window to apply syntax highlighting changes.',
 54→      'Reload Window',
 55→    )
 56→    .then((selection) => {
 57→      if (selection === 'Reload Window') {
 58→        commands.executeCommand('workbench.action.reloadWindow');
 59→      }
