  5/**
  6 * Base element class.
  7 */
  8// eslint-disable-next-line @typescript-eslint/no-extraneous-class
  9class PLDrawingBaseElement {
 10  /**
 11   * Generates a canvas representation of an element from given options.
 12   * This should set up all handlers for saving the element to the submittedAnswer.
 13   * @param _canvas Fabric canvas to create the object onto.
 14   * @param _options Element options
 15   * @param _submittedAnswer Answer state.
 16   */
 17  static generate(_canvas, _options, _submittedAnswer) {
 18    return null;
 19  }
 20
 21  /**
 22   * Function that is called on pressing this element's control button.
 23   * You can usually leave this as the default.
 24   * @param canvas Fabric canvas to create the object onto.
 25   * @param options Element options
 26   * @param submittedAnswer Answer state.
 27   */
 28  static button_press(canvas, options, submittedAnswer) {
 29    return this.generate(canvas, options, submittedAnswer);
 30  }
 31
 32  /**
 33   * Get the filename for this element's button icon.  This is relative to the clientFiles directory.
 34   * You can leave this as the default if your element's icon has the same name as its type.
 35   * @param options Element options.
 36   */
 37  static get_button_icon(options) {
 38    return options.type;
 39  }
 40
 41  /**
 42   * Get the "tooltip" for the element button.  This is displayed when the user hovers their mouse
 43   * over the button.
 44   * @param options Element options.
 45   */
 46  static get_button_tooltip(options) {
 47    return `Add ${options.type}`;
 48  }
 49}
