 51/**
 52 * Base functions to be used by element objects and extensions.
 53 */
 54window.PLDrawingApi = {
 55  _idCounter: 0,
 56
 57  // This will be used to load `.svg` files from the `clientFilesElement` directory.
 58  clientFilesBase: (() => {
 59    const url = new URL(window.document.currentScript.src);
 60
 61    // Strip any search or hash parameters from the URL.
 62    url.search = '';
 63    url.hash = '';
 64
 65    // Strip off the last component of the URL (the script filename).
 66    url.pathname = url.pathname.split('/').slice(0, -1).join('/');
 67
 68    // Add the clientFilesElement directory.
 69    url.pathname += '/clientFilesElement/';
 70
 71    return url.toString();
 72  })(),
 73
 74  /**
 75   * Generates a new numeric ID for a submission object.
 76   * Each submitted object is uniquely identified by its ID.
 77   */
 78  generateID() {
 79    return this._idCounter++;
 80  },
 81
 82  elements: {},
 83  elementModule: {},
 84  /**
 85   * Register a dictionary of elements.  These should map element names
 86   * to a static class corresponding to the element itself.
 87   * Python options dictionary.
 88   * @param extensionName Name of the extension/group of elements.
 89   * @param dictionary Dictionary of elements to register.
 90   */
 91  registerElements(extensionName, dictionary) {
 92    _.extend(this.elements, dictionary);
 93    Object.keys(dictionary).forEach((elem) => {
 94      this.elementModule[elem] = extensionName;
 95    });
 96  },
 97
 98  /**
 99   * Generate an element from an options dictionary.
100   * @param canvas Canvas to create the element on.
101   * @param options Element options.  Must contain a 'type' key.
102   * @param submittedAnswer Answer state object.
103   */
104  createElement(canvas, options, submittedAnswer) {
105    const name = options.type;
106    const added = null;
107
108    if (name in this.elements) {
109      const element = this.elements[name];
110      element.generate(canvas, options, submittedAnswer);
111    } else {
112      console.warn('No element type: ' + name);
113    }
114
115    return added;
116  },
117
118  /**
119   * Get an element definition by its name.
120   * @param name Name of the element to look up.
121   * @returns The element, if found.  Silently fails with the base element otherwise.
122   */
123  getElement(name) {
124    let ret = PLDrawingBaseElement;
125    if (name in this.elements) {
126      const elem = this.elements[name];
127      if ('generate' in elem) {
128        ret = elem;
129      }
130    }
131    return ret;
132  },
133
134  /**
135   * Restore the drawing canvas state from a submitted answer.
136   * @param canvas Canvas to restore state onto.
137   * @param submittedAnswer Answer state to restore from.
138   */
139  restoreAnswer(canvas, submittedAnswer) {
140    for (const [id, obj] of Object.entries(submittedAnswer._answerData)) {
141      this._idCounter = Math.max(Number.parseInt(id) + 1, this._idCounter);
142      const newObj = structuredClone(obj);
143      this.createElement(canvas, newObj, submittedAnswer);
144    }
145  },
146
147  /**
148   * Main entrypoint for the drawing element.
149   * Creates canvas at a given root element.
150   * @param root_elem DIV that holds the canvas.
151   * @param elem_options Any options to give to the element
152   * @param existing_answer_submission Existing submission to place on the canvas.
153   */
154  setupCanvas(root_elem, elem_options, existing_answer_submission) {
155    const canvas_elem = $(root_elem).find('canvas')[0];
156    const canvas_width = Number.parseFloat(elem_options.width);
157    const canvas_height = Number.parseFloat(elem_options.height);
158    const html_input = $(root_elem).find('input');
159
160    const parseElemOptions = function (elem) {
161      const opts = JSON.parse(elem.getAttribute('opts'));
162
163      // Parse any numerical options from string to floating point
164      for (const key in opts) {
165        const parsed = Number(opts[key]);
166        if (!Number.isNaN(parsed)) {
167          opts[key] = parsed;
168        }
169      }
170      return opts;
171    };
172
173    // Set all button icons
174    const drawing_btns = $(root_elem).find('button');
175    const element_base_url = elem_options['element_client_files'];
176    const clientFilesBase = this.clientFilesBase;
177    drawing_btns.each(function (i, btn) {
178      const img = btn.children[0];
179      const opts = parseElemOptions(img.parentNode);
180      const elem = window.PLDrawingApi.getElement(opts.type);
181      const elem_name = opts.type;
182      if (elem !== null) {
183        let image_filename = elem.get_button_icon(opts);
184        if (image_filename !== null) {
185          if (!image_filename.endsWith('.svg')) {
186            image_filename += '.svg';
187          }
188          let base = clientFilesBase;
189          if (window.PLDrawingApi.elementModule[elem_name] !== '_base') {
190            base = element_base_url[window.PLDrawingApi.elementModule[elem_name]] + '/';
191          }
192          img.setAttribute('src', base + image_filename);
193        }
194        const image_tooltip = elem.get_button_tooltip(opts);
195        if (image_tooltip !== null) {
196          btn.setAttribute('title', image_tooltip);
197        }
198        if (!elem_options.editable) {
199          btn.disabled = true;
200        }
201        const cloned_opts = { ...opts };
202        $(btn).click(() => elem.button_press(canvas, cloned_opts, submittedAnswer));
203      }
204    });
205
206    // Render at a higher resolution if requested
207    const renderScale = elem_options['render_scale'];
208    canvas_elem.width = canvas_width * renderScale;
209    canvas_elem.height = canvas_height * renderScale;
210
211    let canvas;
212    if (elem_options.editable) {
213      canvas = new fabric.Canvas(canvas_elem);
214    } else {
215      canvas = new fabric.StaticCanvas(canvas_elem);
216    }
217    canvas.selection = false; // disable group selection
218
219    // Re-scale the html elements
220    canvas.viewportTransform[0] = renderScale;
221    canvas.viewportTransform[3] = renderScale;
222    canvas_elem.parentElement.style.width = canvas_width + 'px';
223    canvas_elem.parentElement.style.height = canvas_height + 'px';
224    $(canvas_elem.parentElement).children('canvas').width(canvas_width);
225    $(canvas_elem.parentElement).children('canvas').height(canvas_height);
226
227    canvas.on('object:added', (ev) => {
228      ev.target.cornerSize *= renderScale;
229      ev.target.borderColor = 'rgba(102,153,255,1.0)';
230    });
231
232    if (elem_options.grid_size > 0) {
233      mechanicsObjects.addCanvasBackground(
234        canvas,
235        canvas_width,
236        canvas_height,
237        elem_options.grid_size,
238      );
239    }
240
241    // Restrict objects from being able to be dragged off-canvas
242    // From: https://stackoverflow.REDACTED
243    canvas.on('object:moving', function (e) {
244      const obj = e.target;
245      // if object is too big ignore,
246      if (obj.currentHeight > canvas_width || obj.currentWidth > canvas_height) {
247        return;
248      }
249      const rect = obj.getBoundingRect(true, true);
250
251      // top-left  corner
252      if (rect.top < 0 || rect.left < 0) {
253        obj.top = Math.max(obj.top, obj.top - rect.top);
254        obj.left = Math.max(obj.left, obj.left - rect.left);
255      }
256      // bot-right corner
257      if (rect.top + rect.height > canvas_height || rect.left + rect.width > canvas_width) {
258        obj.top = Math.min(obj.top, canvas_height - rect.height + obj.top - rect.top);
259        obj.left = Math.min(obj.left, canvas_width - rect.width + obj.left - rect.left);
260      }
261      // snap the element to the grid if enabled
262      if (elem_options.snap_to_grid) {
263        obj.top = Math.round(obj.top / elem_options.grid_size) * elem_options.grid_size;
264        obj.left = Math.round(obj.left / elem_options.grid_size) * elem_options.grid_size;
265      }
266
267      obj.setCoords();
268    });
269
270    fabric.util.addListener(canvas.upperCanvasEl, 'dblclick', function (e) {
271      const target = canvas.findTarget(e);
272      if (target !== undefined) {
273        target.fire('dblclick', { e });
274      }
275    });
276
277    // Restore existing answer if it exists
278    const submittedAnswer = new PLDrawingAnswerState(html_input);
279    if (existing_answer_submission != null) {
280      submittedAnswer._set(existing_answer_submission);
281      window.PLDrawingApi.restoreAnswer(canvas, submittedAnswer);
282    }
283  },
284};
