286/**
287 * Representation of a submitted answer state.
288 * The contents of this are what will be eventually submitted to PrairieLearn.
289 */
290class PLDrawingAnswerState {
291  constructor(html_input) {
292    this._answerData = {};
293    this._htmlInput = html_input;
294  }
295
296  _set(obj_ary) {
297    obj_ary.forEach((object) => {
298      this._answerData[object.id] = object;
299    });
300  }
301
302  _updateAnswerInput() {
303    // Correctly escape double back-slashes... (\\)
304    const temp = JSON.stringify(Object.values(this._answerData)).replaceAll('\\\\', '\\\\\\\\');
305    this._htmlInput.val(temp);
306  }
307
308  /**
309   * Update an object in the submitted answer state.
310   * @param object Object to update.
311   */
312  updateObject(object) {
313    if (object.id in this._answerData) {
314      if (this._answerData[object.id].type !== object.type) {
315        console.trace(
316          `Trying to set id ${object.id} from type ${this._answerData[object.id].type} to ${
317            object.type
318          }`,
319        );
320        console.warn('Existing', this._answerData[object.id]);
321        console.warn('New', object);
322      }
323    }
324    this._answerData[object.id] = object;
325    this._updateAnswerInput();
326  }
327
328  /**
329   * Find an object by its ID.
330   * @param id Numeric id to search by.
331   * @returns The object, if found.  Null otherwise.
332   */
333  getObject(id) {
334    return this._answerData[id] || null;
335  }
336
337  /**
338   * Remove an object from the submitted answer.
339   * @param object The object to delete, or its ID.
340   */
341  deleteObject(object) {
342    if (object != null && typeof object === 'object') {
343      object = object.id;
344    }
345    delete this._answerData[object];
346    this._updateAnswerInput();
347  }
348
349  /**
350   * Registers an object to save to the answer when modified.
351   * This maintains a "submission" object that is separate from the canvas object.
352   * By default, all properties from the canvas object are copied to the submission object.
353   *
354   * @param options Options that were passed to the 'generate()' function.
355   * @param object Canvas object that was created and should be saved.
356   * @param modifyHandler {optional} Function that is run whenever the canvas object is modified.
357   * This has the signature of (submitted_object, canvas_object).
358   * Any properties that should be saved should be copied from canvas_object into
359   * submitted_object.  If this is omitted, all properties from the canvas object
360   * are copied as-is.
361   * @param removeHandler {optional} Function that is run whenever the canvas object is deleted.
362   */
363  registerAnswerObject(options, object, modifyHandler, removeHandler) {
364    const submitted_object = { ...options };
365    if (!('id' in submitted_object)) {
366      if (!('id' in object)) {
367        submitted_object.id = window.PLDrawingApi.generateID();
368      } else {
369        submitted_object.id = object.id;
370      }
371    }
372
373    const blocked_keys = new Set([
374      'aCoords',
375      'borderColor',
376      'cacheHeight',
377      'cacheTranslationX',
378      'cacheTranslationY',
379      'cacheWidth',
380      'canvas',
381      'cornerSize',
382      'dirty',
383      'isMoving',
384      'matrixCache',
385      'oCoords',
386      'ownCaching',
387      'ownMatrixCache',
388      'id',
389    ]);
390
391    this.updateObject(submitted_object);
392    object.on('modified', () => {
393      if (modifyHandler) {
394        modifyHandler(submitted_object, object);
395      } else {
396        for (const [key, value] of Object.entries(object)) {
397          if (key[0] !== '_' && !blocked_keys.has(key)) {
398            submitted_object[key] = value;
399          }
400        }
401      }
402      this.updateObject(submitted_object);
403    });
404    object.on('removed', () => {
405      if (removeHandler) {
406        removeHandler(submitted_object, object);
407      }
408      this.deleteObject(submitted_object);
409    });
410  }
411}
