414(() => {
415  class DrawingDeleteButton extends PLDrawingBaseElement {
416    static get_button_icon() {
417      return 'delete';
418    }
419
420    static get_button_tooltip() {
421      return 'Delete selected object';
422    }
423
424    static button_press(canvas, _options, _submittedAnswer) {
425      canvas.remove(canvas.getActiveObject());
