427  }
428  class DrawingHelpLineButton extends PLDrawingBaseElement {
429    static generate(canvas, options, submittedAnswer) {
430      const def = {
431        left: 40,
432        top: 40,
433        x1: 40,
434        x2: 140,
435        y1: 40,
436        y2: 40,
437        trueHandles: ['mtr', 'ml', 'mr'],
438        stroke: '#0057a0',
439        strokeWidth: 1,
440        strokeDashArray: [4, 4],
441        padding: 10,
442        graded: false,
443      };
444      const opts = { ...def, ...options, type: 'pl-line' };
445      window.PLDrawingApi.createElement(canvas, opts, submittedAnswer);
446    }
447
448    static get_button_icon() {
449      return 'help-line';
450    }
451
452    static get_button_tooltip() {
453      return 'Add help line';
454    }
455  }
456
457  const builtins = {
458    delete: DrawingDeleteButton,
459    'help-line': DrawingHelpLineButton,
460  };
461
462  window.PLDrawingApi.registerElements('_base', builtins);
463})();
