 934mechanicsObjects.LatexText = fabric.util.createClass(fabric.Object, {
 935  type: 'latex-text',
 936  parse(str) {
 937    // Because the MathJax renderer expects text to be already formatted in LaTeX,
 938    // manually parse inputs for $$ and escape non-latex with `\text{}`
 939
 940    let built_str = '';
 941    const spl = str.split('$');
 942
 943    for (let i = 0; i < spl.length; i++) {
 944      if (i % 2 === 0) {
 945        // Text
 946        if (spl[i].length > 0) {
 947          // Ignore empty strings
 948          built_str += '\\text{' + spl[i] + '} ';
 949        }
 950      } else {
 951        // LaTeX
 952        built_str += spl[i] + ' ';
 953      }
 954    }
 955    return built_str;
 956  },
 957  async gen_text(str, options) {
 958    await MathJax.startup.promise;
 959    const svgResult = await MathJax.tex2svgPromise(str, { display: false });
 960    const svg = svgResult.children[0];
 961
 962    // SVG's generated by MathJax reference a list of sharedbase elements,
 963    // so replace each reference with the actual element value.
 964    $(svg)
 965      .find('use')
 966      .each((_, use) => {
 967        // Find and create a new copy to link to
 968        const refLink = use.getAttribute('xlink:href');
 969        const refElement = $(svg).find(refLink)[0];
 970        const replacement = $(refElement.outerHTML)[0];
 971
 972        // Copy over any attributes on the link
 973        for (let i = 0; i < use.attributes.length; i++) {
 974          const attrib = use.attributes.item(i);
 975          if (attrib.name.toLowerCase() !== 'xlink:href') {
 976            replacement.setAttribute(attrib.name, attrib.value);
 977          }
 978        }
 979
 980        // Replace the reference with the actual value
 981        use.parentNode.replaceChild(replacement, use);
 982      });
 983
 984    const exScale = 1 - MathJax.config.svg.fontData.defaultParams.x_height;
 985
 986    // Convert width/height from `ex` units to `px` units. This ensures that
 987    // the image renders consistently regardless of the browser's configured
 988    // font size.
 989    const parsedWidth = Number.parseFloat(svg.getAttribute('width'));
 990    const parsedHeight = Number.parseFloat(svg.getAttribute('height'));
 991    const width = parsedWidth * exScale * options.fontSize;
 992    const height = parsedHeight * exScale * options.fontSize;
 993    svg.setAttribute('width', width + 'px');
 994    svg.setAttribute('height', height + 'px');
 995
 996    let svgSource = svg.outerHTML;
 997
 998    // Fix for Safari, https://stackoverflow.com/questions/30273775/namespace-prefix-ns1-for-href-on-tagelement-is-not-defined-setattributens
 999    svgSource = svgSource.replaceAll(/NS\d+:href/gi, 'xlink:href');
1000
1001    const base64svg = 'data:image/svg+xml;base64,' + btoa(unescape(encodeURIComponent(svgSource)));
1002
1003    fabric.Image.fromURL(
1004      base64svg,
1005      (img) => {
1006        this.width = Math.ceil(width);
1007        this.height = Math.ceil(height);
1008        img.width = this.width;
1009        img.height = this.height;
1010
1011        this.setCoords(false, false);
1012        this.image = img;
1013        this.dirty = true;
