import { chromium, webkit, firefox, Browser, Page } from 'playwright';
import * as fs from 'fs';
import * as path from 'path';
import * as http from 'http';

const HTML_PATH = '/tmp/mathjax-test.html';

async function startServer(): Promise<http.Server> {
  const html = fs.readFileSync(HTML_PATH, 'utf-8');
  const server = http.createServer((req, res) => {
    res.writeHead(200, { 'Content-Type': 'text/html' });
    res.end(html);
  });
  return new Promise((resolve) => {
    server.listen(0, () => resolve(server));
  });
}

async function testBrowser(browserType: any, browserName: string, url: string) {
  console.log(`\n${'='.repeat(60)}`);
  console.log(`Testing: ${browserName}`);
  console.log(`${'='.repeat(60)}`);

  let browser: Browser | null = null;
  try {
    browser = await browserType.launch({ headless: true });
    const context = await browser.newContext();
    const page: Page = await context.newPage();

    // Collect console logs
    const logs: string[] = [];
    page.on('console', (msg) => {
      logs.push(`[${msg.type()}] ${msg.text()}`);
    });

    page.on('pageerror', (err) => {
      logs.push(`[PAGE ERROR] ${err.message}`);
    });

    await page.goto(url, { waitUntil: 'networkidle' });

    // Wait for MathJax to load and test to complete
    try {
      await page.waitForFunction('window.testComplete === true', { timeout: 30000 });
    } catch {
      console.log('  TIMEOUT waiting for test to complete');
    }

    // Get test result
    const result = await page.evaluate(() => (window as any).testResult || 'INCOMPLETE');
    console.log(`  Result: ${result}`);

    // Get the page content with results
    const resultsText = await page.evaluate(() => {
      const el = document.getElementById('results');
      return el ? el.innerText : 'No results element found';
    });
    console.log(`  Output:\n${resultsText.split('\n').map((l: string) => '    ' + l).join('\n')}`);

    // Take a screenshot
    const screenshotPath = `/tmp/mathjax-${browserName}.png`;
    await page.screenshot({ path: screenshotPath, fullPage: true });
    console.log(`  Screenshot: ${screenshotPath}`);

    // Print any errors from console
    const errors = logs.filter((l) => l.includes('ERROR') || l.includes('error'));
    if (errors.length > 0) {
      console.log(`  Console errors:`);
      errors.forEach((e) => console.log(`    ${e}`));
    }
  } catch (err: any) {
    console.log(`  Browser test failed: ${err.message}`);
  } finally {
    if (browser) await browser.close();
  }
}

async function main() {
  const server = await startServer();
  const address = server.address() as any;
  const url = `http://localhost:${address.port}`;
  console.log(`Server running at ${url}`);

  try {
    // Test all three browser engines
    await testBrowser(chromium, 'chromium', url);
    await testBrowser(webkit, 'webkit', url);
    await testBrowser(firefox, 'firefox', url);
  } finally {
    server.close();
  }
}

main().catch(console.error);

